import React, { useState } from "react";
import axios from "axios";

function Home(){

    const [inputs,setInputs]=useState({
        id: '',
        password:''
    });

    const {id,password} = inputs;

    const onChange =(e)=>{
        console.log(e);
        const {name,value} = e.target;
        const nextInputs = {
            ...inputs,
            [name] : value
        }
        setInputs(nextInputs);
    };

    function login(){

        if( id===""||password===""){
            console.log("no id or password ");
            return;
        }else{

            axios.post('http://192.168.100.198:32365/enroll',{
                id: id,
                password:password
            })
            // axios({
            //     method: "POST",

            //     url: "/enroll",
            //     data: {
            //         id: id,
            //         password:password
            //     }
            // })
            .catch(function(error){
                console.log(error);
            })
            .then((res)=>{
                console.log(res);
                alert(res);
            })
            setInputs({
                id:"",
                password:""
            })

        }
        return

    }

    return(
        <div>
            <b>ID</b>
            <input name='id'type='text' placeholder="id" onChange={onChange}/>
            <br></br>
            <b>Password</b>
            <input name='password'type='password' placeholder="password" onChange={onChange}/>
            <br/>
            <button onClick={login}>로그인</button>
        </div>
    );
}

export default Home
